/**
 * @since 0.1.8
 */
import { createInterface } from 'readline';
/**
 * @since 0.1.8
 */
export function getLine(question) {
    return function () {
        return new Promise(function (resolve) {
            var rl = createInterface({
                input: process.stdin,
                output: process.stdout
            });
            rl.question(question, function (answer) {
                rl.close();
                resolve(answer);
            });
        });
    };
}
